#!/bin/bash

# Create simple PNG icons using ImageMagick (if available) or base64 encoded minimal PNGs

# Create a simple 16x16 icon (base64 encoded minimal PNG)
echo "" | base64 -d > icon16.png

# Create a simple 48x48 icon
echo "" | base64 -d > icon48.png

# Create a simple 128x128 icon
echo "" | base64 -d > icon128.png

chmod +x create_icons.sh
